void nextPermutation(vector<int>& nums) {
    int n = nums.size();

    // 1. Find the pivot
    int i = n - 2;
    while (i >= 0 && nums[i] >= nums[i + 1]) {
        i--;
    }

    // 2. If no pivot, reverse entire array
    if (i < 0) {
        reverse(nums.begin(), nums.end());
        return;
    }

    // 3. Find smallest element greater than pivot
    int j = n - 1;
    while (nums[j] <= nums[i]) {
        j--;
    }

    // 4. Swap pivot with that element
    swap(nums[i], nums[j]);

    // 5. Reverse suffix
    reverse(nums.begin() + i + 1, nums.end());
}
