/*
Given a sorted array of integers and a target value, determine if there exist two numbers
whose sum equals the target
*/

#include <iostream>
#include <vector>
using namespace std;

bool hasPairWithSum(vector<int>& nums, int target) {
    int l = 0, r = nums.size() - 1;

    while (l < r) {
        int sum = nums[l] + nums[r];

        if (sum < target) {
            l++;
        } else if (sum > target) {
            r--;
        } else {
            return true;
        }
    }
    return false;
}


int main() {
    vector<int> v = {2, 3, 5, 8, 11};
    int target = 16;

    bool present = hasPairWithSum(v, target);

    cout << "The target is " << present << endl;

    return 0;
}
